import { BoardSquareArgs } from './js/BoardSquareArgs';
import { useEffect, useState } from 'react';
import './App.css';

function App() {

  const [boardArgs, setBoardArgs] = useState<BoardSquareArgs[][]>();
  const [whiteDisplay, setWhiteDisplay] = useState<boolean>(false);
  const [blackDisplay, setBlackDisplay] = useState<boolean>(false);
  const board: BoardSquareArgs[][] = [];

  const whitePieces = document.getElementById("WhiteDisplay")
  const blackPieces = document.getElementById("BlackDisplay");

  if (whitePieces !== null && whitePieces.children.length === 0) {
      setWhiteDisplay(false);
  }

  if (blackPieces !== null && blackPieces.children.length === 0) {
      setBlackDisplay(false);
  }

  function LoadInitialBoard() {
    const pieceOrder = [ "Rook", "Knight", "Bishop", "Queen", "King", "Bishop", "Knight", "Rook" ];
    let whiteSquare = true;
    let whitePiece = false;

    for (let i = 0; i < 8; i++) {
      board.push(new Array<BoardSquareArgs>());

      for (var j = 0; j < 8; j++) {
          board[i][j] = new BoardSquareArgs();

          if (i == 0 || i == 7) {
              board[i][j].piece = (whitePiece ? "white|" : "black|") + pieceOrder[j];
          }
          else if (i == 1 || i == 6) {
              board[i][j].piece = whitePiece ? "white|Pawn" : "black|Pawn";
          }

          board[i][j].col = i;
          board[i][j].row = j;
          board[i][j].backColor = (whiteSquare ? "white" : "black");

          board[i][j].pieceClass = board[i][j].backColor + " boardBtn";

          if (board[i][j].piece && board[i][j].piece != "") 
          {
              board[i][j].pieceClass += " " + board[i][j].piece.split('|').join('');
          }

          whiteSquare = !whiteSquare;
      }

      if (i == 1) {
          whitePiece = !whitePiece;
      }

      whiteSquare = !whiteSquare; 
    };

    setBoardArgs(board);
  }

  useEffect(() => {
    LoadInitialBoard();
  });

  return (
    <>
      <div className="pieceDisplay" style={{display: whiteDisplay ? "block" : "none"}} id="WhiteDisplay"></div>
      <div id="GameBoard" className="GameBoard">
        {boardArgs?.map((row, i) => {
          return (
            <>
              <div className={row[0].pieceClass} id={"board" + row[0].col + row[0].row}></div>
              <div className={row[1].pieceClass} id={"board" + row[1].col + row[1].row}></div>
              <div className={row[2].pieceClass} id={"board" + row[2].col + row[2].row}></div>
              <div className={row[3].pieceClass} id={"board" + row[3].col + row[3].row}></div>
              <div className={row[4].pieceClass} id={"board" + row[4].col + row[4].row}></div>
              <div className={row[5].pieceClass} id={"board" + row[5].col + row[5].row}></div>
              <div className={row[6].pieceClass} id={"board" + row[6].col + row[6].row}></div>
              <div className={row[7].pieceClass} id={"board" + row[7].col + row[7].row}></div>
            </>
          )
        })}
      </div>
      <div className="pieceDisplay" style={{display: blackDisplay ? "block" : "none"}} id="BlackDisplay"></div>
    </>
  );
}

export default App;
