export class BoardSquare {

    col: number;
    row: number;
    piece: string;
    backColor: string;
    pieceClass: string;
    isHighlighted: boolean = false;

    constructor(args : BoardSquareArgs) {
        this.col = args.col;
        this.row = args.row;
        this.piece = args.piece;
        this.backColor = args.backColor;
        this.pieceClass = args.pieceClass;
        this.render();
    }

    render() {
        console.log(this);
        const square = document.createElement('div');
        square.id = "board" + this.col.toString() + this.row.toString();
        square.className = this.pieceClass;
        square.onclick = this.boardClick;
        document.getElementById("GameBoard")?.appendChild(square);
    }

    boardClick() {
        console.log(this.col, this.row);
    }
}
